  v.updateTimeIcon = function () {
    function draw(icon) {
      var ctx = icon.getContext("2d");
      ctx.lineWidth = 2;
      ctx.clearRect(0, 0, 50, 50);
      ctx.beginPath();
      ctx.arc(25, 25, 24, 0, 2 * Math.PI);
      ctx.stroke();
      ctx.beginPath();
      ctx.moveTo(25 + 12 * Math.cos(Math.PI / 6 * (v.hh - 3 + v.mm / 60)), 25 + 12 * Math.sin(Math.PI / 6 * (v.hh - 3 + v.mm / 60)));
      ctx.lineTo(25, 25);
      ctx.lineTo(25 + 20 * Math.cos(Math.PI / 30 * (v.mm - 15)), 25 + 20 * Math.sin(Math.PI / 30 * (v.mm - 15)));
      ctx.stroke();
    }

    timeIcon && draw(timeIcon);
    timeIcon24 && draw(timeIcon24);
  };

